import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Subject, Subscriber } from 'rxjs';
import { Task } from './task.model';
@Injectable({ providedIn: 'root' })
export class TasksService {
  private tasks: Task[] = [];
  private taskUpdated = new Subject<Task[]>();
  constructor(private http: HttpClient) {}
  getTask() {
    this.http
      .get<{ status: {}; data: Task[] }>('http://localhost:3000/api/tasks')
      .subscribe((taskData) => {
        this.tasks = taskData.data;
        this.taskUpdated.next([...this.tasks]);
      });
    // return [...this.tasks];
  }
  getTaskUpdateListener() {
    return this.taskUpdated.asObservable();
  }
  addTask(task: Task) {
    this.http
      .post<{ status: {}; data: Task[] }>(
        'http://localhost:3000/api/tasks',
        task
      )
      .subscribe((res: any) => {
        this.tasks.push(res.data);
        this.taskUpdated.next([...this.tasks]);
      });
  }
  deleteTask(id: String) {
    this.http
      .delete('http://localhost:3000/api/tasks/' + id)
      .subscribe((res) => {
        this.tasks = this.tasks.filter((task) => task._id !== id);
        this.taskUpdated.next([...this.tasks]);
      });
  }
  getTasks(id: string) {
    return this.http.get<{ status: {}; data: Task[] }>(
      'http://localhost:3000/api/tasks/' + id
    );
  }
  updateTask(task: Task) {
    this.http
      .put<{ status: {}; data: Task[] }>(
        'http://localhost:3000/api/tasks/' + task._id,
        task
      )
      .subscribe((res: any) => {
        console.log(res);
        let index = this.tasks.findIndex((t) => t._id == task._id);
        if (index > -1) {
          this.tasks[index] = task;
          this.taskUpdated.next([...this.tasks]);
        }
      });
  }
}
