import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Subject, Subscriber } from 'rxjs';
import { Task } from './task.model';
@Injectable({ providedIn: 'root' })
export class TasksService {
  private tasks: Task[] = [];
  private taskUpdated = new Subject<Task[]>();
  constructor(private http: HttpClient) {}
  getTask() {
    this.http
      .get<{ status: {}; data: Task[] }>('http://localhost:3000/api/tasks')
      .subscribe((taskData) => {
        this.tasks = taskData.data;
        this.taskUpdated.next([...this.tasks]);
      });
    // return [...this.tasks];
  }
  getTaskUpdateListener() {
    return this.taskUpdated.asObservable();
  }
  addTask(task: Task) {
    this.http.post<{ status: {}; data: Task[] }>(
      'http://localhost:3000/api/tasks',
      task.subscribe(() => {})
    );
    this.tasks.push(task);
    this.taskUpdated.next([...this.tasks]);
  }
}
