import { Component, OnInit } from '@angular/core';
import { Task } from '../task.model';
import {
  Form,
  FormControl,
  FormGroup,
  NgForm,
  Validators,
} from '@angular/forms';
import { TasksService } from '../task.service';
import { ActivatedRoute, ParamMap } from '@angular/router';

@Component({
  selector: 'app-task-create',
  templateUrl: './create.component.html',
  styleUrls: ['./create.component.scss'],
})
export class CreateTaskComponent implements OnInit {
  newTask = 'abhi';
  enteredValue = 'Hey';
  enteredTitle = ' ';
  enteredDescription = '';
  public mode = 'create';
  private id: string = null;
  public task: Task;
  isLoading = false;
  taskForm: FormGroup;
  constructor(
    public tasksService: TasksService,
    public route: ActivatedRoute
  ) {}

  ngOnInit() {
    this.taskForm = new FormGroup({
      title: new FormControl(null, {
        validators: [Validators.required, Validators.minLength(5)],
      }),
      description: new FormControl(null, { validators: [Validators.required] }),
    });
    this.route.paramMap.subscribe((res: ParamMap) => {
      if (res.has('id')) {
        this.mode = 'edit';
        this.id = res.get('id');
        this.isLoading = true;
        this.tasksService.getTasks(this.id).subscribe((res: any) => {
          delete res.data.__v;
          this.task = res.data;
          this.isLoading = false;
          this.taskForm.setValue({
            title: this.task.title,
            description: this.task.description,
          });
        });
      } else {
        this.mode = 'create';
        this.id = null;
      }
    });
  }
  onCreateTask() {
    if (!form.valid) return;
    const task: Task = {
      _id: null,
      title: this.taskForm.value.title,
      description: this.taskForm.value.description,
    };
    if (this.mode === 'create') {
      this.tasksService.addTask(task);
    }
    if (this.mode === 'edit') {
      task._id = this.task._id;
      this.tasksService.updateTask(task);
    }
    this.taskForm.reset();
  }
}
<div class="div1">
  <mat-spinner *ngIf="isLoading"></mat-spinner>
</div>
<mat-card *ngIf="!isLoading">
  <form [formGroup]="taskForm" ="onCreateTask()">
    <mat-form-field>
      <input matInput type="text" name="title" formControlName="title" />
      <mat-error *ngIf="taskForm.get('title').invalid">
        Please enter the title</mat-error
      >
    </mat-form-field>
    <mat-form-field class="example-full-width">
      <mat-label>Create Your task </mat-label>
      <textarea
        matInput
        name="description"
        formControlName="description"
        placeholder="Type your task"
      ></textarea>
      <mat-error *ngIf="taskForm.get('description').invalid"
        >Please enter the description</mat-error
      >
    </mat-form-field>
    <button class="btn-1" mat-raised-button color="accent" type="submit">
      {{ mode | uppercase }}
    </button>
  </form>
</mat-card>

